mapper.insert:
INSERT INTO review_likes (
    cno
    , rno
    , uid
) VALUES (
    #{cno}
    , #{rno}
    , #{uid}
)

mapper.delete:
DELETE FROM review_likes
WHERE rno = #{rno}
      AND uid = #{uid}

mapper.select:
SELECT COUNT(rlno)
FROM review_likes
WHERE rno = #{rno}

SELECT COUNT(rlno)
FROM review_likes
WHERE rno = #{rno}
      AND uid = #{uid}
